import React from 'react'
import { motion } from 'framer-motion'

interface NovaMascotProps {
  size?: 'sm' | 'md' | 'lg'
  animate?: boolean
  className?: string
}

const NovaMascot: React.FC<NovaMascotProps> = ({ size = 'md', animate = true, className = '' }) => {
  const sizeClasses = {
    sm: 'w-16 h-16',
    md: 'w-24 h-24',
    lg: 'w-32 h-32'
  }

  const animations = animate ? {
    animate: {
      y: [0, -5, 0],
      rotate: [0, 2, -2, 0],
    },
    transition: {
      duration: 3,
      repeat: Infinity,
      ease: "easeInOut"
    }
  } : {}

  return (
    <motion.div
      className={`${sizeClasses[size]} ${className} relative`}
      {...animations}
    >
      {/* Owl Body */}
      <div className="w-full h-full rounded-full bg-gradient-to-br from-orange-400 to-orange-600 relative overflow-hidden shadow-lg">
        {/* Eyes */}
        <div className="absolute top-2 left-1/2 transform -translate-x-1/2 flex space-x-1">
          <motion.div 
            className="w-4 h-4 bg-white rounded-full relative"
            animate={animate ? { scale: [1, 0.9, 1] } : {}}
            transition={{ duration: 3, repeat: Infinity }}
          >
            <div className="absolute top-1 left-1 w-2 h-2 bg-black rounded-full"></div>
          </motion.div>
          <motion.div 
            className="w-4 h-4 bg-white rounded-full relative"
            animate={animate ? { scale: [1, 0.9, 1] } : {}}
            transition={{ duration: 3, repeat: Infinity }}
          >
            <div className="absolute top-1 left-1 w-2 h-2 bg-black rounded-full"></div>
          </motion.div>
        </div>

        {/* Glasses */}
        <div className="absolute top-1 left-1/2 transform -translate-x-1/2 w-8 h-4 border-2 border-primary-600 rounded-full bg-transparent"></div>

        {/* Beak */}
        <div className="absolute top-6 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-yellow-500 rounded-sm rotate-45"></div>

        {/* Graduation Cap */}
        <div className="absolute -top-2 left-1/2 transform -translate-x-1/2">
          <div className="w-6 h-4 bg-primary-700 rounded-t-lg"></div>
          <div className="absolute -top-1 -right-2 w-1 h-3 bg-secondary-500 rounded-full"></div>
        </div>

        {/* Bow Tie */}
        <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 w-3 h-2 bg-primary-700 rounded-sm"></div>
      </div>
    </motion.div>
  )
}

export default NovaMascot
